document.addEventListener("DOMContentLoaded",(function(){!function(){const e=document.querySelectorAll(".accordion-header");if(0===e.length)return;e.forEach(e=>{e.addEventListener("click",(function(){const e=this.parentElement;if(e.classList.contains("active")){e.classList.remove("active");e.querySelector(".accordion-content").style.maxHeight=null;e.querySelector(".accordion-arrow").style.transform="rotate(0deg)"}else{e.classList.add("active");const t=e.querySelector(".accordion-content");t.style.maxHeight=t.scrollHeight+"px";e.querySelector(".accordion-arrow").style.transform="rotate(180deg)"}}))})}(),function(){const e=document.querySelectorAll(".accordion");if(0===e.length)return;e.forEach(e=>{e.addEventListener("click",(function(){this.classList.toggle("is-open");const e=this.nextElementSibling;e.style.maxHeight?e.style.maxHeight=null:e.style.maxHeight=e.scrollHeight+"px"}))})}()}));